$(document).ready(function(){choose_item();info_manage();set_height()});function choose_item(){$(".main_nav>ul>li").click(function(){$(".i_last").removeClass("fa-chevron-down");$(".i_last").addClass("fa-chevron-right");if($(this).hasClass("active")){$(this).children("ul").slideUp();$(this).removeClass("active")}else{$(".main_nav>ul>li").children("ul").slideUp();$(this).children("ul").slideDown();$(".main_nav>ul>li").removeClass("active");$(this).addClass("active");$(".active .i_last").removeClass("fa-chevron-right");$(".active .i_last").addClass("fa-chevron-down")}})}function info_manage(){$(".info_manage").click(function(){$(this).children("ul").slideDown();$(this).children("ul").delay(2000).slideUp()})}function set_height(){$("#nav").css("height","720px");var b=$("#nav").css("height");b=b.substring(0,b.length-2);var a=$("#main").css("height");a=a.substring(0,a.length-2);if(a-b>0){$("#content").css("height",a);$("#nav").css("height",a)}else{$("#content").css("height",b)}};