$(document).ready(function(){flash_message();reset_form();update();check_display();clickDel();add_detail();selectbox();search();nextstep();toggle();});function flash_message(){$('div.message').delay(5000).slideUp();}function toggle(source){checkboxes=document.getElementsByName('check[]');for(var i=0,n=checkboxes.length;i<n;i++){checkboxes[i].checked=source.checked;}}function reset_form(){$('input#reset_form').click(function(){$("#updateForm input[type='text']").attr('value','');$("#updateForm select option").attr('selected',null);$('#updateForm select option').first().attr('selected','selected');});}function check_display(){$('#check_display').click(function(){if($(this).attr('value')==0){$(this).attr('value','1');}else{$(this).attr('value','0');}});}function add_detail(){$('#add_detail').click(function(){$('#insert').append('<div class="form-group"><div class="col-md-6 col-md-offset-3"><input type="file" name="img_detail[]"  style="margin-top: 5px; border: 1px solid #CCCCCC; width: 100%; border-radius: 5px;"></div></div>');});}function clickDel(){$('a#del_img').on('click',function(){var url= "http://localhost/Project_2/admin/product/delImg/";var _token = $('#updateForm').find('input[name="_token"]').val();var idHinh=$(this).parent().find('img').attr('idHinh');var id = $(this).parent().attr('id');var img = $(this).parent().find('img').attr('src');$.ajax({url: url+idHinh,type:"GET",cache:false,data: {'_token':_token,'idHinh':idHinh,'urlHinh':img},success:function(data){if(data=="OK")$('#'+id).remove();else alert('Không xóa được hình');}});})}
function update(){
	$('.update').click(function(){
		var rowid = $(this).attr('id');
		var type = $(this).attr('idtype');
		var qty = $(this).parent().parent().find('.span1').val();
		var _token = $('input[name="_token"]').val();
		$.ajax({
			url:'http://localhost/Project_2/shopping-cart/update/'+rowid+'/'+qty+'/'+type,
			type:"GET",
			cache:false,
			data:{'_token':_token,'id':rowid,'qty':qty,'type':type},
			success:function(data){
				if(data=="ok"){
					window.location='list';}
				}
			});
	});
}
	function search(){$('#search').autocomplete({source: "http://localhost/Project_2/search/autocomplete",select: function(event, ui) {$('#search').val(ui.item.value);}});}function selectbox(){$('#selectbox, #selectbox1').select2({allowClear:true});}function nextstep(){$('.home').removeClass('active');$('#step2').css('display','none');$('#step3').css('display','none');$('#continue1').click(function(){$('#step1').css('display','none');$('#step2').css('display','block');});$('#continue2').click(function(){$('#step2').css('display','none');$('#step3').css('display','block');});$('#back2').click(function(){$('#step2').css('display','none');$('#step1').css('display','block');});$('#back3').click(function(){$('#step3').css('display','none');$('#step2').css('display','block');});}