$(document).ready(function(){slider();info_member()});function slider(){$(".slider").bxSlider({auto:true,autoControls:true,captions:true,})}function info_member(){$(".info_member").click(function(){if($(this).hasClass("active_info_member")){$(this).children("ul").slideUp();$(this).removeClass("active_info_member")}else{$(this).children("ul").slideDown("slow");$(this).addClass("active_info_member")}})};